css:
 thead > tr> th{
     font-size:10px;
     font-weight:600;
 }
 p{
        text-align:justify;
  }
  .horizontal-line {
  width: 70%;
  border-bottom: 1px solid #000;
  line-height: 0.1em;
  margin: 10px 0 10px;
  text-align: left;
}

.shipper-signature {
  background: #fff;
  padding: 0 10px;

}

html:
{% set declared_value_of_shipped_property = frappe.db.get_single_value('Bill Of Lading Settings JI','declared_value_of_shipped_property') %}
{% set liability_limitation_clause = frappe.db.get_single_value('Bill Of Lading Settings JI','liability_limitation_clause') %}
{% set received_terms = frappe.db.get_single_value('Bill Of Lading Settings JI','received_terms') %}
{% set carrier_get_payment_caluse = frappe.db.get_single_value('Bill Of Lading Settings JI','carrier_get_payment_caluse') %}
{% set dot_regulation_clause = frappe.db.get_single_value('Bill Of Lading Settings JI','dot_regulation_clause') %}
{% set carrier_acknowledgment_receipt = frappe.db.get_single_value('Bill Of Lading Settings JI','carrier_acknowledgment_receipt') %}



<div>
    <table class="table table-bordered" style="width:100%;">

        <tr style="width:100%;">
            <td style="width:50%;font-size:16px;"><b>{{doc.name}}</b></td>
            <td style="width:50%;"><b>Date:</b> {{ frappe.format(doc.date, {'fieldtype': 'Date'}) }}</td>
        </tr>
        <tr style="width:100%;">
            <td style="width:50%;"><b>Ship From: </b><br> {{doc.ship_from_address}}</td>
            <td style="width:50%;"><b>Bill of Lading No:</b> {{doc.name}}<br><br><br><br><br></td>
        </tr>

         <tr style="width:100%;">
            <td style="width:50%;">
                <span><b>PO/REF #: </b> {{doc.ref}} &nbsp;

                </span>
                </td>
            <td style="width:50%;"><b>Carrier Name:</b> {{doc.carrier_name}}</td>
        </tr>

        <tr style="width:100%;">
            <td style="width:50%;">
                <b>Ship To: </b><br> {{doc.shipping_address_name}}<br> {{doc.ship_to}}<br>
                <span>
                    <b>CID# : </b> {{doc.cid}} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% if doc.fob == 1 %}
                        <font style="font-size:16px;">&#x2611;</font> FOB
                    {% else %}
                        <font style="font-size:20px;">&#x25A1;</font> FOB
                    {% endif %}
                </span>
            </td>
            <td style="width:50%;"><b>Pro No:</b> {{doc.pro_no or ''}}</td>
        </tr>

        <tr style="width:100%;">
            <td style="width:50%;"><b>Third Party Freight Charges - Bill To:</b><br>
                {{doc.third_party_freight_charges_bill_to}}
            </td>
            <td style="width:50%;"><b>Freight Charge Terms (prepaid unless marked otherwise):</b><br>
                <span>
                   {% if doc.freight_charge_terms == 'Prepaid' %}
                        <font style="font-size:16px;">&#x2611;</font> Prepaid &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% else %}
                        <font style="font-size:20px;">&#x25A1;</font> Prepaid &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% endif %}
                    {% if doc.freight_charge_terms == 'Collect' %}
                       <font style="font-size:16px;"> &#x2611; </font> Collect &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% else %}
                        <font style="font-size:20px;">&#x25A1;</font> Collect &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% endif %}
                    {% if doc.freight_charge_terms == '3rd Party' %}
                       <font style="font-size:16px;"> &#x2611; </font> 3rd Party &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% else %}    
                        <font style="font-size:20px;">&#x25A1;</font> 3rd Party &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    {% endif %}
                </span>
            </td>
        </tr>

        <tr style="width:100%;">
            <td colspan="2"><b>Special Instructions:</b> {{doc.special_instructions}}</td>
        </tr>

    </table>

    <table class="table table-bordered" style="width:100%;margin-top:-20px;">
            <thead>
                  <tr style="width:100%;">
                    <th style="text-align:center;">Description </th>
                    <th style="">Weight </th>
                    <th style="">Packing Units</th>
                    <th style="">Additional Shipper Info</th>
                    <th style="">NMFC#</th>
                    <th style="">Freight Class</th>
                  </tr>
            </thead>

            <tbody>
            <tr style="width:100%;">
                <td style="width:30%;"> {{ doc.bill_of_lading_details[0].description or '' }} </td>
                <td style=""> {{ doc.bill_of_lading_details[0].weight or '' }} </td>
                <td style="">{{ doc.bill_of_lading_details[0].packing_units or ''}}</td>
                <td style="">{{ doc.bill_of_lading_details[0].additional_shipper_info or ''}}</td>
                <td style="">{{doc.bill_of_lading_details[0].nmfc or '' }}</td>
                <td style="text-align:center; vertical-align:center !important;" rowspan={{doc.bill_of_lading_details|length}}>{{ doc.printed_freight_class or ''}}</td>
            </tr>

            {%- for row in doc.bill_of_lading_details -%}
                  {% if row.idx != 1 %}
                    <tr style="width:100%;">
                        <td style="width:30%;"> {{ row.description or '' }} </td>
                        <td style=""> {{ row.weight or '' }} </td>
                        <td style="">{{ row.packing_units or ''}}</td>
                        <td style="">{{ row.additional_shipper_info or ''}}</td>
                        <td style="">{{row.nmfc or '' }}</td>
                    </tr>
                  {% endif %}
            {%- endfor -%}
        </tbody>


        <tr style="width:100%;">
            <td style="text-align:right;"><b>TOTALS:</b></td>
            <td style="" class="text-uppercase"><b> {{doc.total_weight}} </b> </td>
            <td style="" class="text-uppercase"><b> {{doc.total_cartons}} </b> </td>
            <td style="" class="text-uppercase" colspan="3"><b>Pallet Quantity : {{doc.pallet_quantity}} </b> </td>

        </tr>
    </table>

    <hr style="width:100%;border:1px solid black;margin-top:-10px;">

    <table class="table table-bordered" style="width: 100%;margin-top:-10px;">

    	<tr style="width: 100%;">
    		<td style="width: 50%;font-size:8px;"><p>{{declared_value_of_shipped_property}} <br>
    		    <font style="font-size:11px;"><u><b>{{doc.declared_total_value}}</b></u>  FOB  <u><b>{{doc.declared_total_value_by or ''}}</b></u></font> ".</p></td>
    		<td style="width: 50%;font-size:9px;">
    			<b>COD Amt. &nbsp; $ {{doc.cod_amount}}</b><br><br>
    			<span style="display:inline-block;"><font><b>Fee Terms:</b></font>
    			        {% if doc.fee_terms == 'Prepaid' %}
                           <font style="font-size:12px;">&#x2611;</font> Prepaid &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        {% else %}
                           <font style="font-size:14px;"> &#x25A1; </font> Prepaid &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        {% endif %}
                        {% if doc.fee_terms == 'Collect' %}
                             <font style="font-size:12px;">&#x2611;</font> Collect &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        {% else %}
                            <font style="font-size:14px;"> &#x25A1;</font> Collect &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        {% endif %}
                        {% if doc.fee_terms == 'Customer Check Acceptable' %}
                           <font style="font-size:12px;">&#x2611;</font> Customer Check Acceptable &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                         {% else %}
                             <font style="font-size:14px;">&#x25A1;</font> Customer Check Acceptable &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        {% endif %}
    			</span>
    		</td>
    	</tr>

    </table>

    <table class="table table-bordered" style="width: 100%;margin-top:-20px!important;">
    	<tr style="width: 100%;">
    		<td style="width: 100%;font-size:8px;">
    			<p><b>{{liability_limitation_clause}}</b></p>
    		</td>
    	</tr>
    </table>

    <table class="table table-bordered" style="width: 100%;margin-top:-20px!important;">

    	<tr style="width: 100%;">
    		<td style="width: 50%;font-size:8px;"><p>{{received_terms}}</p></td>
    		<td style="width: 50%;font-size:8px;"><p>{{carrier_get_payment_caluse}} </p><br><br>

    		   <div class="horizontal-line">
                    <span class="shipper-signature">Shipper Signature</span>
               </div>

    		</td>
    	</tr>
    </table>

    <table class="table table-bordered" style="width: 100%;margin-top:-20px!important;">

    	<tr style="width: 100%;height:15%;">
    		<td style="width: 30%;font-size:8px;"><p>{{dot_regulation_clause}} </p><br><br><br>

            	<div style="display:flex;width:100%;">

                   <p style="float:left;width:50%;border-top:1px solid black;">Shipper Signature</p>
                   <p style="width:20%;"></p>
                   <p style="float:right;width:30%;border-top:1px solid black;">Date</p>
                </div>

    		</td>
    		<td style="width: 10%;border-right:0px!important;font-size:8px;">  
    			<fieldset>      
    			        <legend style="font-size: 8px;"><b>Trailer Loaded</b></legend><br>
    			        {% if doc.trailer_loaded == 'By Shipper' %}
                             <font style="font-size:12px;">&#x2611; </font> By Shipper <br>
                        {% else %}
                             <font style="font-size:14px;">&#x25A1; </font> By Shipper <br>
                        {% endif %}
                        {% if doc.trailer_loaded == 'By Driver' %}
                             <font style="font-size:12px;">&#x2611;</font> By Driver <br>
                        {% else %}
                            <font style="font-size:14px;">&#x25A1;</font> By Driver <br>
                        {% endif %}

        		</fieldset>  
    		</td>
    		<td style="width: 20%;border-left:0px!important;font-size:8px;">
    			<fieldset>      
    			        <legend style="font-size: 8px;"><b>Freight Counted</b></legend> <br>
    			         {% if doc.freight_counted == 'By Shipper' %}
                            <font style="font-size:12px;">&#x2611;</font> By Shipper <br>
                        {% else %}
                             <font style="font-size:14px;"> &#x25A1;</font> By Shipper <br>
                        {% endif %}
                         {% if doc.freight_counted == 'By Driver/pallets said to contain' %}
                            <font style="font-size:12px;"> &#x2611;</font>  By Driver/pallets said to contain <br>
                        {% else %}
                             <font style="font-size:14px;"> &#x25A1;</font> By Driver/pallets said to contain <br>
                        {% endif %}
                        {% if doc.freight_counted == 'By Driver/Pieces' %}
                           <font style="font-size:12px;"> &#x2611;</font> By Driver/Pieces <br>
                        {% else %}
                             <font style="font-size:14px;"> &#x25A1;</font> By Driver/Pieces <br>
                        {% endif %}

        		</fieldset>  
    		</td>
    		<td style="width: 40%;font-size:8px;">
    			<p>{{carrier_acknowledgment_receipt}}</p><br><br>

           <div style="display:flex;width:100%;">

                   <p style="float:left;width:50%;border-top:1px solid black;">Carrier Signature</p>
                   <p style="width:20%;"></p>
                   <p style="float:right;width:30%;border-top:1px solid black;">Pickup Date</p>
            </div>


    		</td>
    	</tr>

    </table>


</div>
